<?php/*	* 	@PACKAGE WP CONTEST	*	USE: content of Contests page*/?><div class="wrap">	<h2><?php _e('Editing Reasons', 'wpc'); ?></h2>	<div class="editing_reason_wrap">		<ul class="editing_reasons">			<?php			$all_edited_votes = wpc_edited_vote_entries(10);			if($all_edited_votes){			foreach($all_edited_votes as $edited_vote ){				$entry_id = $edited_vote['entry_id'];				$judge_id = $edited_vote['judge_id'];				$edit_reason = $edited_vote['edit_reason'];				$voting_time = $edited_vote['voting_time'];								$entry_values = wpc_get_entry($entry_id);				$contest_id   = $entry_values['contest_id'];				$sumitted_by =   get_user_by('id', $entry_values['user_id'])->display_name;				$edited_by =   get_user_by('id', $judge_id)->display_name;				$entry_meta = unserialize( $entry_values['entry_meta'] );				$img_src = wpc_upload_dir_url().$entry_meta['image'];						?>			<li class="editing_reasons">				<div class="img_wrap">					<img src="<?php echo $img_src; ?>" alt="" />				</div>				<div class="reason_wrap">					<div class="reason">						<h4 class="contest_name"><b><?php _e('Contest Title:', 'wpc'); ?> </b><?php echo wpc_get_contest_title($contest_id); ?></h4>						<p><?php echo $edit_reason; ?></p>					</div>					<div class="reason_footer">						<span class="__entry_id"><b><?php _e('Entry id', 'wpc'); ?></b> <?php echo $entry_id; ?></span>						<span class="uploaded_by"><b><?php _e('Uploaded by:', 'wpc'); ?></b> <?php echo $sumitted_by; ?></span>						<span class="editing_time"><b><?php _e('Editing Time:', 'wpc'); ?></b> <?php echo $voting_time; ?></span>						<span class="editing_time edited_by"><b><?php _e('Editing By:', 'wpc'); ?></b> <?php echo $edited_by; ?></span>					</div>				</div>			</li>			<?php }			}else{				_e( "No Editing Reason Found.", "pop");			}?>											</ul>	</div></div>