<?php /*  * 	@PACKAGE WP CONTEST*	USE: Save ontest*/if(!function_exists('wpc_save_contest')){function wpc_save_contest(){	global $wpdb;	$table_name = $wpdb->prefix . 'wpc_contests';		if(!current_user_can('manage_options')){		wp_die('You are not allowed to edit this page.');	}	check_admin_referer('wpc_save_contest_verify');	$title = sanitize_text_field($_POST['name']);	if( empty($title) || $title == '' ){		wp_redirect( esc_url_raw( get_admin_url().'admin.php?page=wpc_setting&wpc_error='.urlencode('Please Add Title') ) );		exit();	}		$fields = wpc_conteset_settings_fields();	$phase_two_voting = wpc_voting_criteria();	$wpdb->insert( 						$table_name, 						array( 							'title' => $title, 							'fields' => serialize( $fields ),							'phase_two_voting_creteria' => serialize($phase_two_voting)						), 						array( 							'%s', 							'%s', 							'%s' 						) 			);	$last_entry = $wpdb->get_var( "SELECT id FROM $table_name ORDER BY id DESC LIMIT 1" );	if($last_entry){		wp_redirect(   esc_url_raw( get_admin_url().'admin.php?page=wpc_contests&contest_id='.absint($last_entry) ) );		exit();	}	}}